import React from 'react';
import "./Write.css";
import { useState, useContext } from 'react';
import axios from 'axios';
import { Context } from '../../context/Context';
import { Link } from 'react-router-dom';
import PDF from "../../components/PDF/print";
import Pdf from "react-to-pdf";
const ref = React.createRef()


export default function Write() {
    let postSubmitted = false;
    const [name, setName] = useState("");
    const [age, setAge] = useState("");

    const { user } = useContext(Context);

    const handleSubmit = async (e) => {
        e.preventDefault();
        const newPost = {
            username: user.username,
            name,
            age,
        };

        try {
            const res = await axios.post("/posts", newPost);
            postSubmitted = true;
            console.log(postSubmitted);
            // window.location.replace("/");
        } catch (err) {
            console.log(err);
        }

    };
    const afterSubmit = () => {
        <>

            <Pdf targetRef={ref} filename='post.pdf'>
                {({ toPdf }) => <button onClick={toPdf}>Capture as PDF</button>}

            </Pdf>
        </>
    }
    return (

        <>


            <div className="write">

                <form className="writeForm" onSubmit={handleSubmit}>
                    <nav className="navbar navbar-light" style={{ backgroundColor: "#000080" }}>
                        <div className="container-fluid">
                            <Link className="navbar-brand link">MAHA<span className="ocolor">DIGITAL</span>PORTAL</Link>

                            <div className="d-flex">
                                <button className="bclass">View Balance</button>

                                <input className="form-control me-2" type="search" placeholder="Search" aria-label="Search" />

                            </div>
                        </div>

                    </nav>
                    <div className="row g-3">
                        <div className="col-md-4">
                            <div className="heading2">
                                <h3>GENERAL AFFIDAVIT</h3>
                            </div>
                        </div>
                        <div className="col-md-3">
                            <select className="input form-select" name="inputState">
                                <option selected class="sinput">Gap Certificate</option>
                                <option className="sinput">Maratha Caste Affidative</option>
                                <option className="sinput"></option>
                                <option className="sinput">A</option>
                            </select>
                        </div>
                    </div>
                    <div className="writeFormGroup">

                        <input
                            type="text"
                            // className="writeInput"
                            autoFocus={true}
                            onChange={e => setName(e.target.value)}
                        />

                    </div>
                    <div className="writeFormGroup">
                        <input
                            type="number"
                            //className="writeInput writeText"
                            onChange={e => setAge(e.target.value)} />
                    </div>
                    <button className="writeSubmit" type="submit">Publish</button>
                </form>

            </div>
            <div>
                <button onClick={afterSubmit()}>Capture</button>
            </div>
        </>
    );
}
