import React from 'react';
import Pdf from "react-to-pdf";
const ref=React.createRef()
const PDF=(props)=>{
    return(
        <>
            <div  ref={ref}>
            <h3>hello</h3>
              <h1>{props.name}</h1>
              <h1>{props.age}</h1>
            </div>

            <Pdf targetRef={ref} filename='post.pdf'>
                {({ toPdf })=><button onClick={toPdf}>Capture as PDF</button>}

            </Pdf>
        </>
    )
}
export default PDF;
