import React from 'react';
import "./Write.css";
import { useState,useContext } from 'react';
import axios from 'axios';
import { Context } from '../../context/Context';
import { Link } from 'react-router-dom';

export default function Write() {
    const [name,setName]=useState("");
    const [age,setAge]=useState("");
    
    const {user}=useContext(Context);

    const handleSubmit=async (e)=>{
        e.preventDefault();
        const newPost={
            username:user.username,
            name,
            age,
        };
       
        try{
            const res=await axios.post("/posts",newPost);
            window.location.replace("/");
        }catch(err){}
        
    };
    return (
        <div className="write">
     
          <form className="writeForm" onSubmit={handleSubmit}>
           <nav className="navbar navbar-light">
        <div className="container-fluid">
          <Link className="navbar-brand link">MAHA<span className="ocolor">DIGITAL</span>PORTAL</Link>
         
          <div className="d-flex">
            <button className="bclass">View Balance</button>
            
            <input className="form-control me-2" type="search" placeholder="Search" aria-label="Search"/>
           
    </div>
        </div>
        
      </nav>
      <div class= "row g-3" style="height: 80px;margin-top: 20px;">
        <div class="col-md-4">
        <div class="heading2">
          <h3>GENERAL AFFIDAVIT</h3>
        </div>
      </div>
        <div className="col-md-3">
        <select className="input" name="inputState" className="form-select">
          <option selected class="sinput">Gap Certificate</option>
          <option className="sinput">Maratha Caste Affidative</option>
          <option className="sinput"></option>
          <option class="sinput">A</option>
        </select>
    </div>
      </div>
                <div className="writeFormGroup">
                   
                    <input
                     type="text"
                     // className="writeInput"
                      autoFocus={true}
                      onChange={e=>setName(e.target.value)}
                      />

                </div>
                <div className="writeFormGroup">
                    <input
                    type="number"
                    //className="writeInput writeText"
                    onChange={e=>setAge(e.target.value)}/>
                </div>
                <button className="writeSubmit" type="submit">Publish</button>
            </form>

        </div>
    );
}
